using IronOcr;
using LockedBitmapUtil;
using System;
using System.Drawing;
using System.Drawing.Imaging;
using System.Text;
namespace ByPASSCAPTCHA
{
    class Program
    {

        static void Main (string[] args)
        {

            string hinhanh = "data:image/svg+xml;base64,";
            string s = GiaiCaptCha(hinhanh);
            Console.WriteLine(s);
        }

        static string GiaiCaptCha (string hinhanh)
        {

            string de = Base64Decode(hinhanh.Replace("data:image/svg+xml;base64,",""));
            File.WriteAllText("anh.svg", de);
            var converter = new GroupDocs.Conversion.Converter("anh.svg");
            var convertOptions = converter.GetPossibleConversions()["png"].ConvertOptions;
            converter.Convert("output.png", convertOptions);
            
            var Ocr = new IronTesseract(); 
            Ocr.Language = OcrLanguage.English;
            Ocr.Configuration.TesseractVersion = TesseractVersion.Tesseract5;
            using (var Input = new OcrInput())
            {
                Input.AddImage("output.png");
                var Result = Ocr.Read(Input);
                Console.WriteLine(Result.Text);  
                return Result.Text;
            }
       
        }
        
        static string Base64Decode(string base64EncodedData)
        {
            var base64EncodedBytes = System.Convert.FromBase64String(base64EncodedData);
            return System.Text.Encoding.UTF8.GetString(base64EncodedBytes);
        }
    }
}
